def get_bnb_price():
    try:
        json = requests.get(
            "https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&ids=binancecoin&order=market_cap_desc"
            "&per_page=100&page=1&sparkline=false").json()
    except:
        return None
    return json[0]['current_price']


